# We store as lines with \u escapes to avoid any console/editor encoding issues.

def write_payload(stem: str, lines: list[str]):
    joined = '\n'.join(lines).strip() + '\n'
    img_sha = hashlib.sha256(joined.encode('utf-8')).hexdigest()
    payload = {
        'source_file_hash': DOC_HASH,
        'image_sha256': img_sha,
        'filename': f"{stem}.png",
        'lines': [ln for ln in lines if ln.strip()],
    }
    out = base / f"{img_sha[:16]}_{stem}.json"
    out.write_text(json.dumps(payload, ensure_ascii=False, indent=2), encoding='utf-8')
    print('wrote', out)

