        "\u0625\u0646 \u0645\u0646 \u0627\u0644\u0628\u064a\u0627\u0646 \u0644\u0633\u062d\u0631\u0627 (\u0635\u062d\u064a\u062d \u0627\u0644\u0628\u062e\u0627\u0631\u064a)",
        "\u0645\u0646 \u0633\u0644\u0643 \u0637\u0631\u064a\u0642\u0627 \u064a\u0644\u062a\u0645\u0633 \u0641\u064a\u0647 \u0639\u0644\u0645\u0627 \u0633\u0647\u0644 \u0627\u0644\u0644\u0647 \u0644\u0647 \u0628\u0647 \u0637\u0631\u064a\u0642\u0627 \u0625\u0644\u0649 \u0627\u0644\u062c\u0646\u0629 (\u0635\u062d\u064a\u062d \u0645\u0633\u0644\u0645)",
        "\u0645\u0646 \u064a\u0631\u062f \u0627\u0644\u0644\u0647 \u0628\u0647 \u062e\u064a\u0631\u0627 \u064a\u0641\u0642\u0647\u0647 \u0641\u064a \u0627\u0644\u062f\u064a\u0646 (\u0645\u062a\u0641\u0642 \u0639\u0644\u064a\u0647)",
        "\u0627\u062a\u0642\u0648\u0627 \u0641\u0631\u0627\u0633\u0629 \u0627\u0644\u0645\u0624\u0645\u0646\u060c \u0641\u0625\u0646\u0647 \u064a\u0646\u0638\u0631 \u0628\u0646\u0648\u0631 \u0627\u0644\u0644\u0647 (\u0623\u062e\u0631\u062c\u0647 \u0627\u0644\u062a\u0631\u0645\u0630\u064a \u0631\u0642\u0645 3127).",
